import React from 'react'
import {pipe, uniq, unnest, values } from 'ramda'

const Books = ({ show, books, setGenreFilter, fax, filter }) => {

  if (!show) {
    return null
  }

  if (books.loading) {
    return <div>loading...</div>
  }

  if (fax.loading) {
    return <div>loading...</div>
  }

  const allGenres = books.data.allBooks.map(a => a.genres)

  const uniqueGenres = pipe(
    values,
    unnest,
    uniq
  )

  const uniques = (uniqueGenres(allGenres))

  return (
    <div>
      <h2>books</h2>

      {filter === "" ?
      null :
      <div>
        in genre <em>filter</em>
      </div>}

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {fax.data.allBooks.map(a => 
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
      {uniques.map(g => 
        <button key = {g} 
        onClick={() => setGenreFilter({g})}>{g}</button>
          )
        }
        <button onClick = {() => setGenreFilter({g: ""})}>all books</button>
      </div>
    </div>
  )
}

export default Books
