import React, { useState } from 'react'
import {pipe, uniq, unnest, values } from 'ramda'
import { useQuery } from 'react-apollo'
import { gql } from 'apollo-boost'

const BOOKS_BY_GENRE = gql`
query allBooksGenre($genre: String!)
{
  allBooks(genre: $genre) {
    title
    author {
      name
      born
    }
    published
    genres
  }
}
`

const Books = ({ show, books }) => {
  const [filter, setFilter] = useState({g: ""})
  const [booksByGenre] = useQuery(BOOKS_BY_GENRE)

  let filteredBooks = booksByGenre({
    variables: filter.g
  })

  if (!show) {
    return null
  }

  if (books.loading) {
    return <div>loading...</div>
  }

  const allGenres = books.data.allBooks.map(a => a.genres)

  const uniqueGenres = pipe(
    values,
    unnest,
    uniq
  )

  const uniques = (uniqueGenres(allGenres))

  console.log(uniques)

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {filteredBooks.data.allBooks.map(a => 
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
      {uniques.map(g => 
        <button key = {g} 
        onClick={() => setFilter({g})}>{g}</button>
          )
        }
      </div>
    </div>
  )
}

export default Books
