import React, { useState } from 'react'
import Select from 'react-select'

const Authors = ({ authors, show, editAuthor }) => {

  const [name, setName] = useState('')
  const [bornString, setBornString] = useState('')
  // if (authors.data === null) {
  //   options = []
  // } else if (authors.data === undefined) {
  //   options = []
  // } else {
  //   authors.data.allAuthors.map(a => {
  //     console.log(a)
  //    return options.concat({value: a.name, label: a.name})
  //   })
  // }

  const [selectedOption, setSelectedOption] = useState(null)
  const [optionPlaceholder, setOptionPlaceholder] = useState('')
  // const [options, setOptions] = useState([])





  if (!show) {
    return null
  }
  if (authors.loading) {
    return <div>loading...</div>
  }

  // let newOptions = authors.data.allAuthors.map(a => a.name)
  // console.log(newOptions)
  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
  ];
  
  

  const submit = async (e) => {
    e.preventDefault()
    const born = Number(bornString)
    await editAuthor({
      variables: {name, born }
    })

    setName('')
    setBornString('')

  }

  const handleChange = selectedOption => {
    setSelectedOption( { selectedOption })
    setOptionPlaceholder(selectedOption.value)
    console.log(selectedOption)
  }

  return (
    <div>
      <h2>authors</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              born
            </th>
            <th>
              books
            </th>
          </tr>
          {authors.data.allAuthors.map(a =>
            <tr key={a.name}>
              <td>{a.name}</td>
              <td>{a.born}</td>
              <td>{a.bookCount}</td>
            </tr>
          )}
        </tbody>
      </table>
      
      <form onSubmit={submit}>
      <Select 
        value = {selectedOption}
        onChange = {handleChange}
        options = {options}
        placeholder = 'awererae'
      />
          <div>
            name <input
              value={name}
              onChange={({ target }) => setName(target.value)}
            />
          </div>
          <div>
            born <input
              value={bornString}
              onChange={({ target }) => setBornString(target.value)}
            />
          </div>
          <button type='submit'>change birthyear</button>
        </form>
    </div>
  )
}

export default Authors
