import React, { useState } from 'react'

const Books = ({ show, books }) => {
  const [genreList, setGenreList] = useState([])
  if (!show) {
    return null
  }
  if (books.loading) {
    return <div>loading...</div>
  }

  let genres = []

  books.data.allBooks.map(a =>
    genres.concat(a.genres)
  )
  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.data.allBooks.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  )
}

export default Books
