import React, { useState } from 'react'
import {pipe, uniq, unnest, values, filter } from 'ramda'
import { useQuery } from 'react-apollo'
import { gql } from 'apollo-boost'

const Books = ({ show, books }) => {
  const [genreFilter, setGenreFilter] = useState({g: ''})

  let filteredBooks

  if (!show) {
    return null
  }


  if (genreFilter.g === '') {
    filteredBooks = books.data.allBooks
  } else {
    filteredBooks = books.data.allBooks
    let f = filter(x => x.genres.includes(genreFilter.g), filteredBooks)
    filteredBooks = f
  }
  if (books.loading) {
    return <div>loading...</div>
  }

  const allGenres = books.data.allBooks.map(a => a.genres)

  const uniqueGenres = pipe(
    values,
    unnest,
    uniq
  )

  const uniques = (uniqueGenres(allGenres))

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {filteredBooks.map(a => 
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
      {uniques.map(g => 
        <button key = {g} 
        onClick={() => setGenreFilter({g})}>{g}</button>
          )
        }
        <button onClick = {() => setGenreFilter({g: ''})}>all books</button>
      </div>
    </div>
  )
}

export default Books
