import React, { useState } from 'react'

const Books = ({ show, books }) => {
  const genreFunction = (books) => {
    if (books === undefined) {
      return []
    }
    return books.data.allBooks.map(a =>
      a.title)
  }
  
  const [genreList, setGenreList] = useState(genreFunction())

  if (!show) {
    return null
  }
  if (books.loading) {
    return <div>loading...</div>
  }


  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.data.allBooks.map(a => 
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
      {books.data.allBooks.map(a => 
            a.genres.map(g => 
              <button key = {g}>{g}</button>
            )
          )
        }
      </div>
    </div>
  )
}

export default Books
